eft join SF_SJ_TechnoSpeciRev ggsps on xmbh.Id = ggsps.ProjCodeApplication_Idleft join SF_SJ_TechDealCompile jsxy on xmbh.Id = jsxy.ProjCodeApplication_Idleft join SF_SJ_DesignInformation sjtz on xmbh.Id = sjtz.ProjectCodeIdleft join SF_SJ_DesDocumentPro wjjs on xmbh.Id =wjjs.ProjectCodeIdleft join SF_SJ_TechnicalDocuments wjhs on xmbh.Id = wjhs.ProjectCodeIdleft join SF_SJ_DesFinishManagement cp on xmbh.Id = cp.ProjectCodeIdleft join SF_SJ_EmitDesignResults sjcg on xmbh.Id = sjcg.ProjectCodeIdleft join SF_SJ_ProjectSummarize sjzj on xmbh.Id = sjzj.ProjectCodeIdwhere xmbh.Status=50select * from View_SF_SJ_Overview--alter table SF_SJ_TechnoSpeciRev add ProjCodeApplication_Id uniqueidentifier null--alter table SF_SJ_TechnoSpeciRev add ProjCodeApplication_Code nvarchar(200) null--alter table SF_SJ_TechDealCompile add ProjCodeApplication_Id uniqueidentifier null--alter table SF_SJ_TechDealCompile add ProjCodeApplication_Code nvarchar(200) nullselect * from PB_Widget where id='82d2ba26-7204-473b-a6f6-ee2f73b9dab8'select Code from SF_SJ_ProjNumberApplication group by Code having count(Code)>1select Title from SF_SJ_ProjNumberApplication group by Title having count(Title)>1select * from PLN_project where project_name in(select project_name from PLN_project group by project_name having count(project_name)>1) order by project_shortnameSelect * From View_SF_SJ_Overview Where xmbhCode like '%11%' and DesignStage like '%可研%' Order By xmbhCodeselect * from PB_Userselect * from pb_human where name='喻本宏'select * from pb_human where id='415d10e8-20d2-439a-9399-6b95d1dcd950'select * from PB_Human where Name='文锦雄'select * from SF_SJ_ProjNumberApplicationselect Name from PB_Human group by name having count(Name)>1